    private void bAceptarMiliActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bAceptarMiliActionPerformed
        //VALIDACION DE LOS MILIMETROS
        try {
            String milimetros = txtMili.getText();

            if (milimetros == null) {
                throw new Exception("POR FAVOR DIGITE ALGO");
            }
            double mil = Double.parseDouble(milimetros);

            this.setVisible(false);

        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, e);
            JOptionPane.showMessageDialog(null, "FAVOR DIGITE UN VALOR VALIDO");
        }


    }//GEN-LAST:event_bAceptarMiliActionPerformed

